    const handleInputChange = () => {
      setValue(value + 1)
    };

    useEffect(() => {
      document.title = `Вы нажали ${value} раз`;
      console.log("after")
    });
    return (
      <div className="App">
        <Button onClick={() => setValue(value + 1)}>Click {value}</Button> 
      </div>
    );
  }
