import React, { useRef, useEffect } from 'react';
import {Form, Button} from 'react-bootstrap'


export default function Example() {
    const count = useRef(0);

    const handleClick = () => {
      console.log(count)
      count.current += 1
    };

    useEffect(() => {
      document.title = `Вы нажали ${count.current} раз`;
    });
    return (
      <div className="App">
        <Button onClick={handleClick}>Click {count.current}</Button> 
        {/* <Button onClick={}>Show value {count.current}</Button>  */}
      </div>
    );
  }
